/*
*    Stylish (userstyles) Dark mode #2D2D2D - Version 1.0 [08/02/2019]
*    https://userstyles.org/styles/
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org")
{

  *
  {
    color: #eeeeee !important;
    background-color: #2d2d2d !important;
}

* {border-color: #6B6B6B !important} /* Couleur bordure */
:link, :link * {color: #FFFFFF !important} /* Lien */
input, textarea {background-color: #3d3d3d !important} /* Fond champ */
:visited, :visited * {color: rgb(220, 180, 120) !important} /* Lien ouvert */
}
