css:
@-moz-document url-prefix("https://www.epraise.co.uk") {
#container {
    background-color: black !important;
}
body {
    color: #ffffff !important;
}
text {
    fill: rgb(255, 255, 255);
}
.classnote_content {
    color: #000 !important;
}
.classnote_subject {
    color: #000 !important;
}
.display_options li a.selected {
    background: #fff !important;
}
.item_list .item_description {
    color: #000 !important;
}
html {
    background-color: black !important;
}
.stat {
    color: #000 !important;
}
.stat_description {
    color: #000 !important;
}
td {
    color: #000 !important;
}
.layout.light {
    background: #000;
}
h2 {
    color: #386d92;
}
p {
    color: #386d92;
}
.classnote_data, .classnote_actions {
    color: #000 !important;
}
}
