@-moz-document domain("calendar.google.com") {
 
/* main area: columnheader has a empty div element before these cells. */
*[role="main"] *[role="presentation"] *[role="columnheader"]:nth-child(2) { background-color: /*[[color1]]*/; }
*[role="main"] *[role="presentation"] *[role="columnheader"]:nth-child(3) { background-color: /*[[color2]]*/; }
*[role="main"] *[role="presentation"] *[role="columnheader"]:nth-child(4) { background-color: /*[[color3]]*/; }
*[role="main"] *[role="presentation"] *[role="columnheader"]:nth-child(5) { background-color: /*[[color4]]*/; }
*[role="main"] *[role="presentation"] *[role="columnheader"]:nth-child(6) { background-color: /*[[color5]]*/; }
*[role="main"] *[role="presentation"] *[role="columnheader"]:nth-child(7) { background-color: /*[[color6]]*/; }
*[role="main"] *[role="presentation"] *[role="columnheader"]:nth-child(8) { background-color: /*[[color7]]*/; }

/* main area: schedule area. */
*[role="main"] *[data-column-index="0"] { background-color: /*[[color1]]*/; }
*[role="main"] *[data-column-index="1"] { background-color: /*[[color2]]*/; }
*[role="main"] *[data-column-index="2"] { background-color: /*[[color3]]*/; }
*[role="main"] *[data-column-index="3"] { background-color: /*[[color4]]*/; }
*[role="main"] *[data-column-index="4"] { background-color: /*[[color5]]*/; }
*[role="main"] *[data-column-index="5"] { background-color: /*[[color6]]*/; }
*[role="main"] *[data-column-index="6"] { background-color: /*[[color7]]*/; }

/* mini calendar area: columnheader */
*[role="grid"] > *[role="row"] > *[role="columnheader"]:nth-child(1) { background-color: /*[[color1]]*/; }
*[role="grid"] > *[role="row"] > *[role="columnheader"]:nth-child(2) { background-color: /*[[color2]]*/; }
*[role="grid"] > *[role="row"] > *[role="columnheader"]:nth-child(3) { background-color: /*[[color3]]*/; }
*[role="grid"] > *[role="row"] > *[role="columnheader"]:nth-child(4) { background-color: /*[[color4]]*/; }
*[role="grid"] > *[role="row"] > *[role="columnheader"]:nth-child(5) { background-color: /*[[color5]]*/; }
*[role="grid"] > *[role="row"] > *[role="columnheader"]:nth-child(6) { background-color: /*[[color6]]*/; }
*[role="grid"] > *[role="row"] > *[role="columnheader"]:nth-child(7) { background-color: /*[[color7]]*/; }

/* mini calendar area: cell must be transparent that is not-selected nor not-today. */
*[role="grid"] *[role="rowgroup"] > *[role="row"] > *:not(*[aria-selected="true"]) > div:not(*[aria-hidden]) { background-color: transparent; }

/* mini calendar area: date area. */
*[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(1) { background-color: /*[[color1]]*/; }
*[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(2) { background-color: /*[[color2]]*/; }
*[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(3) { background-color: /*[[color3]]*/; }
*[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(4) { background-color: /*[[color4]]*/; }
*[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(5) { background-color: /*[[color5]]*/; }
*[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(6) { background-color: /*[[color6]]*/; }
*[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(7) { background-color: /*[[color7]]*/; }

}
