css:
@-moz-document domain("fabricmc.net") {
body {
    background: #111;
    color: #999;
}

select,
button {
    background: #313131;
    border: 1px solid #212121;
    color: #9f9f9f;
}

#dw__toc{
    background: #212121;
    border: 1px solid #414141;
}

.dokuwiki .page li .li, .dokuwiki .aside li .li {
    color: #999;
}

.dokuwiki div.page {
    background: #212121;
    border: 1px solid #8a7b7b;
    box-shadow: none;
}

#dokuwiki__header h1 a,
.site-nav .page-link,
.site-title,
.site-title:visited {
    color: #f0f0f0;
}
}
