css:
@-moz-document domain("gog.com") {
* {
	background-color: #0000!important;
	color: #fff!important;
}
html,
body,
._cs-left,
._cs-right {
	background-image: linear-gradient(#0000, #0000)!important;
	background-color: #000!important;
}
.fade,
.wrapper,
.module_ex1,
.module_ex2,
.menu-submenu,
[role="button"],
.job-listings-footer,
.galaxy-tooltip__trigger,
.user-statistics > div > span,
.title__additional-options > div > .dropdown__trigger {
	background: #0000!important;
	background-image: linear-gradient(#0000, #0000)!important;
}
.module,
.button,
textarea,
[role="button"],
.arrow-wrapper,
.cdp-offers-btn,
.dropdown__layer,
._dropdown__items,
.account__filters,
.department-offers,
.product-row__text,
.dropdown__trigger,
.menu-section-layer,
.menu-submenu::after,
.discover-games-more,
.menu-search__results,
.product-tile__content,
.search-input-container,
.carousel-pagination__page,
.prof-custom-post-editor__wrapper {
	background-color: #333!important;
	background-image: linear-gradient(#0000, #0000)!important;
}
svg {
	fill: #fff!important;
}
.login:hover,
.carousel__page,
.menu-scrollbar__bar,
.menu-submenu-item.menu-submenu-item--hover:hover .menu-submenu-link {
	background-color: #777!important;
}
::-webkit-scrollbar {
	width: 10px;
}
::-webkit-scrollbar-track {
	background: #777;
}
::-webkit-scrollbar-thumb {
	background: #333;
}
::-webkit-scrollbar-thumb:hover {
	background: #444;
}
}
