css:
@-moz-document domain("tumblr.com") {
.identity, .l-container.l-container--two-column-dashboard .l-content, .l-container.l-container--two-column-dashboard .left_column, .l-container.l-container--two-column .l-content, .l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column .right_column, .l-header, .l-content, .left_column, .right_column, .post_avatar, .post_permalink, .post_avatar .post_avatar_link, .post_full .post_permalink, .l-header-container--refresh {
    background-color: #222;
    border-top-color: #222;
    border-right-color: #222;
}
.compose-button {
    fill: rgb(216, 216, 216);
    opacity: .80;
}
.identity .controls_section.user_list li .follow_list_item_blog:before, .identity .controls_section.user_list li:active .follow_list_item_blog:before, .identity .controls_section.user_list li:hover .follow_list_item_blog:before, .radar .radar_footer .radar_avatar::before {
    background-image: none;
    border-right: none;
}
.sidebar_link.explore_link {
    color: hsla(0,0%,100%,.4)
}
.plus-follow-button.blue, .plus-follow-button:hover {
    background: #ddd !important;
    color: rgba(0, 0, 0, 0.5) !important;
}
}
