@-moz-document domain("avito.ru") {
    html {filter:invert(94%); background-color:#0f0f0f;}
    body {background-color:#fff;}
    
    .button-origin:active,
    .button-origin.is-active {box-shadow:none;}
    
    img,
    .b-shop-soc,
    .seller-info-avatar,
    .item-socials-share,
    .gallery-list-item-link,
    .messenger-item-preview,
    .gallery-extended-list-item-link,
    div[style^="background-image"],
    .messenger-channel-item-preview {filter:invert(94%);}
    
    .serp-vips,
    .js-mini-messenger,
    .js-common-banner,
    .avito-ads-container,
    .profile-item-promo,
    .profile-item-warning,
    .profile-item-lifetime-bar,
    .profile-item-cart-wrapper,
    div[class^="index-advert"],
    .filters-submit-wrapper_fixed-2tB-P {display:none;}
    
    .profile-item {margin-bottom:20px;}
    .tabs-item {padding:0 10px 10px 0;}
    .styles-avatar-2DYvK::before {background:none;}
}
