css:
@-moz-document url-prefix("https://www.roblox.com") {
.ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard{display:none !important}.ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard{display:none !important}

body {
    background: transparent !important;
  }
  .content {
    background: transparent !important;
  }
  #fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("http://imgur.com/Se2ZvxO.jpg") !important; /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
                    }

.rbx-header {
    background-color: #bd0016 !important;
    font-size: 16px !important;
    height: 40px !important;
}

.section-content {
    background-color: #352f2f !important;
    padding: 15px !important;
    position: relative !important;
}

.game-card-container {
    background-color: #352f2f !important;
    position: relative !important;
    border-radius: 3px !important;
    float: left !important;
    height: 100% !important;
    margin: 0 auto !important;
    padding: 0 !important;
    text-align: left !important;
    width: 100% !important;
}

.rbx-left-col {
    background-color: #352f2f !important;
}

.container-footer {
    background-color: #352f2f !important;
}

.profile-accoutrements-container .profile-accoutrements-slider .accoutrement-item a {
    background-color: #352f2f !important;
}

.slide-switcher .slide-item-container .slide-item-container-right {
    background-color: #352f2f !important;
}

.slide-switcher.groups {
    background-color: #bd0016 !important;
}

.para-overflow-toggle .toggle-para {
    color: #bd0016 !important;
}

.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
    box-shadow: 0 -4px 0 0 #bd0016 inset !important;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    background-color: #352f2f !important;
}

.rbx-tabs-horizontal .rbx-tab .rbx-tab-heading:hover, .rbx-tabs-horizontal .rbx-tab .rbx-tab-heading:focus, .rbx-tabs-horizontal .rbx-tab .rbx-tab-heading:active, .rbx-tabs-horizontal .rbx-tab .rbx-tab-heading.active {
    box-shadow: 0 -4px 0 0 #352f2f inset !important;
}

.dialog-container .dialog-header {
    background-color: #bd0016 !important;
    color: #352f2f !important;
}

.chat-main .chat-header {
    background-color: #352f2f !important;
}

.avatar-card-container {
    background-color: #352f2f !important;
    position: relative;
}

.avatar-back {
    width: 277px;
    height: 352px;
    background-image: none;
    background-size: 352px 352px;
    margin-left: -15px;
    margin-top: -15px;
    position: relative;
    overflow: hidden;
}

.section-content {
    background-color: #00000030 !important;
    padding: 15px !important;
    position: relative !important;
}

.item-card-container {
    background-color: #352f2f !important;
    position: relative;
    border-radius: 3px;
    padding: 0 0 5px;
    width: 126px;
    max-width: 150px;
    height: 100%;
}
}
