css:
@-moz-document domain(zhihu.com) {
    .RichContent-inner figure{
        width:25%;
    }
    .RichContent-inner figure img:hover{
        position:relative;
        transform:scale(2) translate(25%,0);
        box-shadow: 0 30px 100px 0 rgba(0, 0, 0, 0.4);
        z-index:9;
		border-radius:.5rem;
    }
    .RichContent-inner,.RichContent-inner * {
        overflow:visible
    }
    .Pc-word{
        display:none;/*去广告*/
    }
}
