css:
@-moz-document url-prefix("https://www.loups-garous-en-ligne.com/jeu/") {
#block_chat {
	color: #ededed;
	background-color: #222;
}

#global {
	background: #111;
}

.canal_meneur {
	color: #71a1ed;
}

.canal_center {
	color: #c95050;
}

#block_chat_post .chat_send {
	background-color: #191919;
}

#block_chat_post.disabled .chat_send {
	background-color: #191919;
}

.background-image {
    background-image: none;
}

.canal_perso {
    color: #bcbcbc;
}

.night {
    background: #111;
}

.clouds1, .clouds2, .clouds3 {
    opacity: 0.3;
}

#block_chat_post input {
    color: #ededed;
}
}
