css:
@-moz-document domain("feynmanlectures.caltech.edu") {
/* common */
body {
  background-color: rgb(33, 33, 33) !important;
  color: rgb(192, 192, 192) !important;
}

a {
  color: rgb(147, 188, 209) !important;
}
a:hover {
  color: rgb(160, 180, 200) !important;
}
a.disabled,
a:hover.disabled {
  color: unset !important;
}
a.badlink,
a:hover.badlink {
  color: rgb(155, 13, 13) !important;
}
a.otherchapter {
  color: rgb(20, 100, 252) !important;
}
a:hover.otherchapter {
  color: rgb(40, 120, 255) !important;
}

.sep {
  border-color: rgb(72, 72, 72) !important;
  background-color: rgb(72, 72, 72) !important;
}

ol#footnotes {
  border-top-color: rgb(48, 66, 81) !important;
}


/* main website subjects*/
h1 {
  color: rgb(255, 255, 255) !important;
}


/* cover */
img[src="img/flp.png"] {
  filter: invert(90%) brightness(160%);
}


/* navigator */
div.floating-menu {
  background-color: rgb(33, 33, 33) !important;
}


/* imagure buttons */
img[src^="img/camera"][src$=".svg"],
img[src^="img/twit"][src$=".png"],
img[src^="img/fb"][src$=".png"],
img[src^="img/email"][src$=".png"],
#styles img,
#styles #MobileLink {
  filter: invert(90%) hue-rotate(38deg) grayscale(40%) brightness(89%);
}
img[src^="img/camera"][src$=".svg"]:hover,
img[src^="img/twit"][src$=".png"]:hover,
img[src^="img/fb"][src$=".png"]:hover,
img[src^="img/email"][src$=".png"]:hover,
#styles #MobileLink:hover {
  filter: invert(90%) hue-rotate(53deg) grayscale(40%) brightness(88%);
}


/* font size bottons */
div.floating-menu a:hover {
  color: rgb(160, 180, 200) !important;
}

div.floating-menu a:active {
  color: rgb(10, 156, 110) !important;
}


/* volume toc structures */
#toc-header > #title {
  color: rgb(255, 255, 255) !important;
}

#toc-controls {
  color: rgb(240, 240, 80, 0.7) !important;
}

.triangle {
  color: rgb(75, 123, 80) !important;
  cursor: pointer !important;
}

#credits {
  color: rgb(174, 174, 174) !important;
}


/* chapter structures */
.chapter-title {
  border-bottom-color: rgb(48, 66, 81) !important;
  color: rgb(209, 232, 255) !important;
}

.summary {
  background-color: rgb(153, 153, 138) !important;
}

.nosummary {
  background-color: unset !important;
}

.section-title {
  color: rgb(226, 226, 226) !important;
}


/* figure image colors */
img[src$=".svgz"] {
  filter: invert(80%);
}


/* floatable elements */
.MathJax_Display,
.figure,
.table {
  background-color: rgb(33, 33, 33) !important;
  color: rgb(192, 192, 192) !important;
}
/*[[eqb_lighter]]*/
/*
=== BEG:eqb_lighter_yes ===
.MathJax_Display,
.figure,
.table {
  background-color: rgb(37, 39, 42) !important;
}
=== END:eqb_lighter_yes ===
=== BEG:eqb_lighter_no ===
=== END:eqb_lighter_no ===
*/
.footnote {
  background-color: rgb(33, 33, 33) !important;
  color: rgb(192, 192, 192) !important;
}
.MathJax_Display[style*="background: rgb(255, 255, 153)"],
.figure[style*="background: rgb(255, 255, 153)"],
.table[style*="background: rgb(255, 255, 153)"],
.footnote[style*="background: rgb(255, 255, 153)"] {
  background-color: rgb(5, 60, 26) !important;
}
.popup.MathJax_Display[style*="background: rgb(255, 255, 170)"],
.popup.figure[style*="background: rgb(255, 255, 170)"],
.popup.table[style*="background: rgb(255, 255, 170)"],
.popup.footnote[style*="background: rgb(255, 255, 170)"]{
  background-color: rgb(6, 73, 31) !important;
}
div.popup {
  background-color: rgb(6, 73, 31) !important;
}


/* tabular */
.tabular,
.tabular tbody,
.tabular thead,
.tabular th,
.tabular td {
  border-top-color: rgb(192, 192, 192) !important;
  border-bottom-color: rgb(192, 192, 192) !important;
  border-left-color: rgb(192, 192, 192) !important;
  border-right-color: rgb(192, 192, 192) !important;
}
}
