css:
@-moz-document domain("intellij-support.jetbrains.com") {
body
{
    background-color: #171717;
}
.vote-sum, .article-content h3 
{
        color: #afafaf;
}
.actions .dropdown-toggle:after 
{
    color: #afafaf;
}

.zendesk .article-content, a._color-black, .article-content h1, .article-content h2, .recent-activity-header, .collapse .category-tree__subtitle,
.navbar.navigation-navbar .navbar__link, .zendesk pre.prettyprint
{
    color: white;
}

.form-field > label
{
    color: white;
}

.recent-activity-item-link
{
    color: #18a3fa;
}

.upload-dropzone span
{
    color: #747474;
}

#request_custom_fields_360000004124_hint
{
    color: white;
}

.collapse .category-tree__subtitle:hover, .recent-activity-item-parent:hover
{
    background-color: #a3a3a3;
}
}
