css:
@-moz-document url-prefix(https://www.eldarya)

{

  #pet-headerPortrait-base
  {
    background: url("http://image.noelshack.com/fichiers/2019/09/5/1551439396-draflayelicon.png") !important;
    background-size: auto 73px !important;
  }

  #pet-portrait
  {
    background: url("http://image.noelshack.com/fichiers/2019/09/5/1551439175-draflayelbackground.png") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"]
  {
    display: none !important;
  }

  #pet-info-food > img
  {
    background: url("http://image.noelshack.com/fichiers/2019/09/5/1551439670-fioledraflayel.png") !important;
    background-size: 60px 60px !important;
    margin-top: -5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
  }

  #pet-info-food
  {
    font-size: 0 !important;
  }

  #pet-info-food > span
  {
    font-size: 16px !important;
}
#pet-info-food:before {
content : "/*[[gktrbkbk]]*/" !important ;
font-size : 20px !important ;
}
#pet-info-food > div {
line-height: 0px;
font-size: 0px;
margin: 13px 0 0 100px;
}
#pet-info-racegender:before {
content : "Draflayel" !important ;
font-size : 14px !important ;
line-height : 20px !important ;
}
#pet-info-racegender {
font-size : 0 !important ;
margin-top : 5px ;
}
#pet-info-name:before {
content : "/*[[kgrrtbkt]]*/" !important ;
font-size : 20px !important ;
}

#pet-info-name {
font-size : 0 !important ;
}


}
@-moz-document regexp("https://www\\.eldarya\\.(com|fr|pl|de|dk|es|hu|it|nl|ro|ru|se|com.br|uk)/player/profile//*[[rijbgibjr]]*/"), url(https://www.eldarya.fr/player/profile/Edelwe%C3%AFnn)
{
#player-display-pet
{
    background: url("http://image.noelshack.com/fichiers/2019/09/5/1551440986-draflayel2-1.png") no-repeat;
    background-size: auto 100%;
    height: 665px !important;
    width: 533px !important;
  }

  #player-display-pet > img
  {
    display: none !important;
  }

}
