css:
@-moz-document url-prefix(http://www.chibiakumas.com/z80/)
{
:root{
    --bgColor: #fefefe;
    --fontColor: #454545;
}

a{
    color:#07a;
}
a:visited{
    color:#ac5a82;
}


body{
    font-family:Open Sans,Arial;
    color:var(--fontColor);
    font-size:16px;
    margin:2em auto;
    max-width:900px;
    padding:1em;
    line-height:1.4;
    text-align:justify;
    background-color:  var(--bgColor);
}

table, td, tr, th{
    color: var(--fontColor);
}

big{
    color: var(--fontColor);
}

.bochan, .chibiko, .yume, .yumi, .sakuya, .red, .green, .blue, .grey, .yellow, .cyan, .magenta{
    color: var(--fontColor)
}
}
