css:
@-moz-document domain("youtube.com"){
.html5-video-container {
    height: 100% !important;
    background-color: black !important;
}

.html5-main-video {
    width: 100% !important;
    height: 100% !important;
    top: 0 !important;
    right: 0 !important;
    bottom: 0 !important;
    left: 0 !important;
}

#player {
    --ytd-watch-flexy-height-ratio: 9 !important;
    --ytd-watch-flexy-width-ratio: 16 !important;
}

.html5-video-player:not(.ytp-fullscreen) .ytp-tooltip, .html5-video-player:not(.ytp-fullscreen) .ytp-bottom, .html5-video-player:not(.ytp-fullscreen) .ytp-preview{
    top: 58% !important;
    bottom:13% !important;
}

}
