css:
/* width */
::-webkit-scrollbar {
    width: 7px;
}

/* Track */
::-webkit-scrollbar-track {
    background: #0000;
}

/* Handle */
::-webkit-scrollbar-thumb {
    background: #535353;
    /*border-radius: 2px;*/
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    background: #373737;
}

