css:
@-moz-document url-prefix("https://raddle.me/") {
:root {
    --bg-dark: #1A1A1B;
    --bg-medium: #333;
    --medium-grey: #818384;
    --light-grey: #D7DADC
}

body {
    background-color: var(--bg-dark);
}

.page-heading,
.forum-name-heading {
    color: var(--light-grey);
}

a.submission-link {
    color: var(--light-grey);
}

p.submission-info {
    color: var(--text-muted);
}

.submission-info {
    color: var(--medium-grey)
}

.submission-host {
    color: var(--medium-grey)
}

nav.submission-nav a,
.comment-nav-reply a {
    color: var(--text-muted) !important;
}

.submission-permalink-nav a {
    color: var(--light-grey);
}

.tabs__bar {
    background-color: var(--bg-medium);
}

.tabs__link {
    color: var(--light-grey);
}

section.sidebar__section,
details.sidebar__section {
    background-color: var(--bg-medium);
    color: var(--medium-grey);
}

.sidebar__title {
    color: var(--light-grey);
}

.form__help {
    color: var(--medium-grey);
}

.sidebar__section li {
    color: var(--medium-grey);
}

.sidebar__section a {
    color: #E72C21;
}

.site-footer {
    color: var(--medium-grey);
}

.vote-score {
    color: var(--light-grey);
}

.form__label {
    color: var(--medium-grey);
}

input, textarea {
    background-color: var(--bg-medium) !important;
    color: var(--light-grey) !important;
}

article.submission {
    color: var(--light-grey);
}

h2 {
    color: var(--light-grey);
}

.comment {
    background-color: var(--bg-dark);
    border: 1px solid var(--bg-medium);
    color: var(--light-grey);
}


}
