css:
@-moz-document url("http://projects.wojtekmaj.pl/react-lifecycle-methods-diagram/") {
body {
	background-color: #252525;
}
.Section {
	border: 1px solid #6a6a6a;
	background-color: #565656;
}

.DiagramWithLegend .Section__title:nth-of-type(1) {
	color: deepskyblue;
}

.DiagramWithLegend .Section__title:nth-of-type(2) {
	color: greenyellow;
}

.DiagramWithLegend .Section__title:nth-of-type(3) {
	color: coral;
}
.Initiator {
	color: #fff
}
.Phase {
	color: #ccc;
	border-bottom: 2px dotted #ccc;
}
.Phase:not(.Phase--last) {
	border-bottom: 2px dotted #ccc;
}
.Section__highlight {
	background-color: #4a4a4a;
}
.Options {
	color: #ccc;
}
.Arrow {
	border-left: 1px dashed #ababab;
}
.Arrow:after {
	background: #ababab;
}
footer {
	color: #ccc;
}
a {
	color: #ccc;
}
svg {
	fill: #ccc;
}
input + label {
	vertical-align: middle;
}
label + select {
	vertical-align: middle;
}
}
