css:
@-moz-document domain("youtube.com"), domain("") {
.caption-visual-line
{
    color: #FFFFFF;
    font-family: "Segoe UI";
    text-shadow: 0px 0px 1px #000000, 0px 0px 2px #000000, 0px 0px 3px #000000, 0px 0px 5px #000000, 0px 0px 9px #000000, 0px 0px 20px #000000;
}

.caption-window
{
    background-color: transparent;
    opacity: 0.9;
}
.ytp-caption-segment
{
    font-family: "Segoe UI" !important;
    background-color: transparent !important;
    color: #FFFFFF !important;
    font-weight: normal !important;
    font-size: 2.4em !important;
}
}
