css:
@-moz-document url-prefix("https://rpi.logicamodernapproach.com") {
body {
    background-color: #262626;
    color: #ffffff !important;
}
.modal-title {
    color: #000;
}
}

@-moz-document url("https://rpi.logicamodernapproach.com/"), url-prefix("https://rpi.logicamodernapproach.com/view") {
.panel-default {
    background-color: #262626 !important;
}
div.col-lg-10 > span {
    background-color: #204d74 !important;
}
span[style*="color:gray"] {
    color: #ff0000 !important;
}
}

@-moz-document url-prefix("https://rpi.logicamodernapproach.com/hyperslate/") {
.table-striped a {
    color: #337ab7 !important;
}
.table-striped td span {
    color: #fff !important;
}
.table-striped td a > span {
    color: #337ab7 !important;
}
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #262626;
}
.popover {
    background-color: #fff !important;
    color: #000 !important;
}
.popover .pull-left {
    background-color: #000;
}
}

@-moz-document url("https://rpi.logicamodernapproach.com/checkScore") {
.panel-default {
    background-color: #262626 !important;
}
div.col-lg-10 > span {
    background-color: #204d74 !important;
}
span[style*="color:gray"] {
    color: #ff0000 !important;
}
tr {
    color: #000;
}
th {
    background-color: #fff;
}
p.text-center {
    color: #000;
}
}

@-moz-document url-prefix("https://rpi.logicamodernapproach.com/leaderboard") {
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #262626;
}
}

@-moz-document url-prefix("https://rpi.logicamodernapproach.com/problems"), url-prefix("https://rpi.logicamodernapproach.com/studentProfile") {
.panel {
    color: #000 !important;
}
}

@-moz-document url-prefix("https://rpi.logicamodernapproach.com/editor") {
#diagramContainer {
 overflow: hidden;
 height: 15000px;
}
}
