css:
@-moz-document domain("au.ru") {
.au-lot-list-card-owner {
    margin-right: 20%;
}

.au-date-marker__item {
    color: #ce0a13;
}

.au-lot-list-card-bottom__left {
    flex: 1 1 71% !important;
}

.au-lot-list-card-price_lot {
    text-align: right;
}

.au-lot-list-card-price {
    display: block;
    text-align: right;
}

.au-lot-list-card-top__right {
    display: none;
}

.au-lot-list-card-title {
    width: 78%;
}

.au-lot-list-card-top__left {
    flex-direction: initial !important;
}

.au-lot-list-card-price_lot {
    width: 22%;
    text-align: end;
}

.au-lot-list-card__time-display {
    float: right;
}

.au-lot-list-card-price__counts, .au-lot-list-card-price__current {
    display: initial;
}

.au-lot-list-card-photo_card-size .au-lot-list-card-photo__img {
    height: 125px;
}

.au-lot-list-card-photo a {
    border: 0;
}

.au-lot-list-card-photo_card-size:after {
    border:0;
    bottom:0;content:"";
    left:0;
    position:absolute;
    right:0;
    top:0
}

.au-card-list-item:nth-child(even) {
    background-color: rgba(234, 234, 234, .5);
}

.au-lot-list-card__promotion {
	display: none;
}

.au-lot-list-card-type-icon {
	display: none;
}

.au-lot-list-card-price__blitz{
	margin-right: 13px;
}

.au-bids-label{
	margin-right: 13px;
}
}
