css:
@-moz-document domain("news.ycombinator.com") {
body {
	background-color: #000;
}

#hnmain {
	color: #FFF;
    background-color: #111;
}

.title,
a:link,
.c00,
.c00 a:link {
	color: #FFF;
}

.pagetop > a:link {
	color: #000;
}

.comhead, .comhead a:link,
.subtext, .subtext a:link {
    color: #999;
}

a:visited {
	color: #999 !important;
}

.pagetop a:visited {
	color: #000 !important;
}
}
