css:
@-moz-document domain("www.iplocation.net") {
	body, #wrapper, .header_main, h1, h2, h3, div.page_title div {
		background-color: #444 !important;
	}

	.col_4_of_12, .col_6_of_12, .col_12_of_12 p, .cbola-layer {
		display: none !important
	}

	.col_8_of_12 {
		width: 100%
	}

	#header, #footer, .clearfix, .col_8_of_12 > div:last-child, .col_8_of_12 > div:nth-last-child(2) {
		display: none !important;
	}

	#wrapper {
		padding-top: 50px;
	}

	#copyright {
		bottom: 0;
		width: 100%;
		position: fixed;
	}
}
