css:
@-moz-document domain("os.phil-opp.com") {
body {
    background-color: #222;
    color: #ddd;
}

div.post-category {
    background-color: #222;
    margin: 0px;
    padding-right: 8px;
    border: solid 2px;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    border-bottom: none;
    font-weight: bold;
}

div.content {
    background-color: #333;
}

h1,
h2,
h3,
h4,
h5,
h1.masthead-title a,
.post-title a,
.post-title {
    color: #eee;
    text-shadow: #000 0px 0px 4px;
}

a,
a code {
    color: #88f;
    text-shadow: #000 0px 0px 4px;
}

code,
pre {
    background-color: #333;
    box-shadow: #000 0px 0px 4px;
    border-radius: 4px;
}

img {
    background-color: #666;
}

strong {
    color: #eee;
}

table {
    border: solid #666 2px;
    box-shadow: #000 0px 0px 4px;
}
table * {
    border: none;
}
tbody {
    border-top: solid #666 2px;
}

tbody tr:nth-child(odd) td,
thead tr:nth-child(even) th {
    background-color: #444;
    border-left: solid #666 2px;
}
tbody tr:nth-child(even) td,
thead tr:nth-child(odd) th {
    background-color: #333;
    border-left: solid #666 2px;
}
}
