css:
@-moz-document regexp("http(s?):\\/\\/lonkaars\\.github\\.io\\/karenbot-io(.*)") {
body {
    --k-white: #fff3dc;
    --k-white-50: #fff3dc88;
    --k-black: #ff6467;
    --k-black-50: #ff646788;
    --k-black-15: #ff646731;
    --k-blue: #55d4fc;
    --k-red: #ee5656;
    --k-red-50: #ee565688;
}
.inviteButton, .commandsButton {
    color: var(--k-white);
    background-color: var(--k-black)
}
.inviteButton:hover, .commandsButton:hover {
    color: var(--k-black);
    background-color: var(--k-white)
}
.imgR {
    filter: brightness(0.5);
}
.scrollToTopWrapper {
    background-color: #ff888a
}
}
