css:
@-moz-document domain(redmine.org) {
textarea.wiki-edit {
    min-height: 600px;
}
.status-2 {
    border-right: 4px solid #00BB00;
    border-left: 4px solid #00BB00;
    background-color: #dfffdf;
}
.status-2.even {
    background-color: #e5fae5;
}
.overdue, .overdue.status-1 {
    border-right: 4px solid #FFC107;
    border-left: 4px solid #FFC107;
    background-color: #ffefe1;
}
.list .overdue .subject::before, .list .overdue.status-1 .subject::before {
    content:"!!";
    font-weight: bold;
    border: none;
    color: #FF5500;
    border-radius: 0;
    margin-right: 6px;
    margin-left: 1px;
    text-indent: 0;
    transform: none;
}
}
