css:
@-moz-document domain("www.blognone.com") {
#wrapper,
body {
    background: #f5fbff;
}
#header {
    height: 120px;
}

#rightbottom_sidebar,
#above_comments,
#navi-menu {
    display: none;
}
#main-content {
    width: 720px;
}
.block .content {
    padding: 20px;
}
.content {
    font-size: 1.1em !important;
    line-height: 1.6em;
    color: #444;
}
.node {
    margin-bottom: 30px;
    border: none;
}
.content-title-box {
    position: relative;
    padding: 20px;
    background: #386386;
    border-bottom: 5px solid #fbc400;
    margin: 0px;
    margin-bottom: 15px;
    border-radius: 0;
    font-size: 1.2em;
    line-height: 1.8em;
}

.news-info {
    border-top: none;
    padding: 15px 10px;
    background: #fbc400;
}
.user_badges {
    display: none;
}
}
