css:
@-moz-document domain(highexistence.com)
{
  
  /* Backgrounds */

#header, body.single-post #header 
{ background-color: #111 }

body, 
.ajax-modal .ajax-content
{ background-color: #222 }

.dropdown-menu,
.comments_section,
.comment, .comment.depth-3, .comment.depth-5, .comment.depth-7, .comment.depth-9,
.ajax-modal .inner, 
#sidebar-single,
form#filter_form .filter_div_items
{ background-color: #333 }

.whiteBG
{ background-color: #333 !important; }

.comment.depth-2
{ background-color: #383838}

.comment.depth-2>div .admin-links
{ background-color: #383838 !important; }

/* Colors */

#nav>ul>li>a,
.listingsContainer .listings .listing>div.title h3 a.title, h1,
h1, .h1, h2, .h2, h3, .h3,
#sidebar span.title,
.reply-meta .bbp-author-name, .reply-meta .reply-author,
h4, .h4, h5, .h5, h6, .h6,
.ajax-modal .ajax-content h1
{ color: #CCC }

.postHeader h1
{ color: #CCC !important; }

.dropdown-menu>li>a,
#nav>ul>li.search ul input,
.subfeatured .topic p,
.subfeatured .quote p,
.post-content,
.post-content p strong,
.reply-text
{ color: #999 }

.subfeatured .quote .author,
.sidebar a .title,
.sidebar a.topic .replies,
.listingsContainer .listings .listing>div.title p,
.reply-meta .karma,
.hethens-voting-wrapper .hethens-voting-vote-counter
{ color: #666 }

/* Border Colors */

.dropdown-menu>li,
.comment.depth-2
{ border-color: #282828 }

.subfeatured .quote,
#sidebar a,
#sidebar, #sidebar .ad,
.authorBox,
.comment.depth-1:last-of-type,
.comment, .comment.depth-3, .comment.depth-5, .comment.depth-7, .comment.depth-9,
.ajax-modal .ajax-content
{ border-color: #181818 }

/* Others */

blockquote { background-image: none}
  
  
}
