css:
@-moz-document url-prefix("https://devblogs.microsoft.com/oldnewthing") {
.c-uhfh > div:first-child {
    background-color: #999988;
}

#single-wrapper {
    background-color: #DDDDCC;
}

.postcontent {
    background-color: #EDEDDD !important;
}

.entry-title {
    color: #770000;
}


.cp3 {
    background-color: white;
}

.entry-area {
    background-color: #FFFFEE;
    border: 1px solid black;
}

.container-fluid {
    background-color: #DDDDCC;
}

.featuresection {
    background-image: none !important;
    background-color: #999988;
}

.herocontent {
    padding: 1em 0;
}

.heroicon {
    padding: 0;
    display: none;
}

.entry-title a {
    color: #700;
}

.entry-image {
    display: none;
}

.entry-avatar {
    display: none;
}

.entry-meta {
    font-size: 0.7em;
}
}
