css:
@-moz-document domain('www.pesdb.net'), domain("pesdb.net")
{
html > body > div#page > div#header > div:nth-child(1) {
display:none !important;
}
html > body > div#page > div#header > div:nth-child(3) {
display:none !important;
}

  div[class="shortcuts"], div[class="friend-banners"],
  iframe[id="shortcuts"]
  {
    display: none !important;  
  }

a[href$="./"]::before
  {
    content: "HOMEPAGE";
  }

  a[href$="./"] > img
  {
    display: none;
  }

  div[class="ad728x90"]
  {
    display: none !important;  
  }
}
