css:
@-moz-document url-prefix("https://forums.frontier.co.uk/") {
/* For forum listings */
.node-icon {
    padding: 0 0 0 15px;
}

.node-main {
    padding: 0 0 0 15px;
}

.node-stats {
    padding: 0;
}

.node-extra {
    padding: 0 0 0 10px;
}

/* Thread listings within a forum */
.structItem-cell--icon {
    padding: 7px 0 0 15px;
}

.structItem-cell--main {
    padding: 1px 0px 2px 3px;
}

.structItem-cell--meta {
    padding: 1px 0 0 10px;
}

.structItem-cell--latest {
    padding: 1px 0 0 5px;
}

/* For a thread */
.message-cell--user {
    padding: 1px 0 2px 3px;
}

.message-cell.message-cell--main {
    padding: 1px 5px 2px 5px;
}

.message-content, .message-signature, .message-footer {
    padding: 0px 0px 0px 0px;
    margin-top: 2px;
}

.message-body {
    padding: 3px 0px 3px 0px;
    margin-top: 2px;
}

.bbCodeBlock--quote {
    margin-top: 2px;
    margin-bottom: 4px;
    padding: 0px 0px 0px 2px;    
}

.bbCodeBlock-title, .bbCodeBlock-content {
    margin-top: 2px;
    margin-bottom: 2px;
    padding: 0px 0px 0px 2px;
}

.message-actionBar .actionBar-set {
    margin-top: 2px;
}

.message .reactionsBar {
    margin-top: 2px;
    padding: 2px;    
}
}
