css:
@-moz-document domain("lofter.com") {
.footer ~ * {
    display:none !important;
}
}

@-moz-document domain("blog.sina.com.cn") {
  #selectionShare ~ * {
      display:none !important;
  }
}

@-moz-document domain("douban.com") {
#wrapper ~ * {
    display:none !important;
}
}

@-moz-document domain("blog.csdn.net") {
#article_content {
    height:auto !important;
}
li.write-bolg-btn  #writeGuide,
.meau-gotop-box ~ *,.pulllog-box,
.hide-article-box ,
body.nodata  > #passportbox 
{
    display:none !important;
}
}


@-moz-document domain("360doc.com") {
#articlecontent {
    height:auto !important;
}
.article_showall {
    display:none !important;
}
}

/* 老子就不注册知乎 */
@-moz-document domain("zhihu.com") {
div > div > div >.Modal-enter-done,
  .Modal-enter-done {display: none !important;}
}

@-moz-document domain("userstyles.org") {
.overlay_background, .android_button_banner {
    display:none !important;
}
}
