css:
@-moz-document url-prefix("https://jira.squiz.net/") {
#timersList, #customfield_10510-val {
    z-index: 999;
    position: fixed;
    top: 50px;
    right: 34px;
    width: 27.7%;
    background-color: white;
    box-shadow: 0px 0px 5px 5px white;   
}

#timersList dl {
    display: block;
}

#timersList dd {
    display: block;
    margin: 0;
    padding: 0;
}

#timersList .aui-button, #customfield_10510-val a {
    overflow: hidden;
    border-radius: 3px;
    padding: 4px 10px;
    background-color: rgba(9,30,66,0.13);
    font-size: small;
    display: block;
    color: black;
}


#timersList .aui-button {
    width: 100%;
}
}
