css:
@-moz-document domain(blitz.bg) {
    body.no-js,
    #page-container {
        background:#222 !important;
    }

    #new-header {
        background:#222 !important;
    }

    #hotnews ul a, #glavni ul a {
        color: #959959;
    }
  .post-content {
		color: #d6d6d6;
  }


    .top-small-article .info h2,
    .box-big h2,
    .featured-header h2,
    .post-title,
    #blitz-hits .media h2 {
        color: #d8d8d8;
    }
    .simple-post h3 {
        color: #bababa;
    }

    #home-top-news .big-article h1,
    #mainNav .container > ul > li > a {
        color: #aba9a9;
    }

    .footer,
    .footer-bottom {
        background: #0b1523;
    }

    .footer-main p {
        color: #858585;
    }

    .sidebar-list .news-story-number::after {
        background: #222 !important;
    }

    .news-details {
        background: #716f6f;
    }
}
