css:
@-moz-document domain("screenshots.firefox.com") {
.app-body {
    background: #111;
    color: #666;
}

.header {
	color: #666;
}

.sub-header {
	color: #666;
}

.header-panel {
    background-color: #000;
}

.close-preferences {
	background-color: #222;
}

.shot-info {
    color: #666;
}

.shot-alt-actions {
    background-color: #111;
}

.annotation-tools {
    background: #000;
}

.editor-header .cancel {
    background-color: #000;
    border: 1px solid #444;
}

.button.secondary {
    background-color: #000;
    color: #989898;
}

.button.primary {
    background-color: #002a54;
    color: #00a5ff;
}

.shot {
    background: #000;
}
}
