css:
@-moz-document domain(elderscrollsonline.wiki.fextralife.com) {

	/* Hide Twitch Player */
	.wiki-link,
	.hidden-sm.fixme > .simulation-hero-heading.section-heading-light.section-heading {
		display: none !important;
	}

	/* Adjust sidebar up to scaling */
	@media (min-width: 1200px) { 
		#wrapper {
			padding-left: 0px !important;
		}

		#sidebar-wrapper {
			width: 0px
		}
	}
}
