css:
@-moz-document url-prefix(https://en.uesp.net/wiki/Special:EsoBuild) {

	.ecdBuildRowHover {
		background: #222
	}
	.ecdBuildRowHover:hover {
		background: #313237 !important;
	}

	.pBody {
		border : none !important;
	}

	th,
	html,
	code,
	input,
	li a,
	table.wikitable th,
	.pBody,
	.toc,
	.navbox table th,
	.catlinks,
	#globalWrapper,
	#footer,
	#content,
	#column-content,
	#mw-content-text table {
		background: #222 !important;
	}

	.esotbAttrTitle {
		background-color: #2f2f2f
	}

	.esotbItemIcon {
		background-color: transparent !important;

	}
	div#content {
		color: #777 !important;
	}

	a, h1, h2, h3, h4, p, h5, tr, th, li, span,
	input, dd, dl {
		color : #999 !important;
	}

	td {
		border : none !important;
	}

	.esovsSkillBarHighlight,
	.esotbWeaponSelect {
		background-color : #999;
		border           : none !important;
		color            : #000;
	}

	.esptbItemSetup select,
	.esotbStatComputeButton,
	.esotbStatDetailsButton,
	.esotbItemButton,
	.esotbItemTransmuteList {
		color            : #000;
		background-color : #999 !important;
	}
}
