css:
@-moz-document url-prefix("https://www.badprog.com/") {
body {
    background-color: black;
}
#main-wrapper, h1, h2, h3, h4, h5, h6, h7, h8, h9, p {
    background-color: #121212;
    color: #CCCCCC;
}
#main-wrapper a {
    color: green !important;
}
#main-wrapper textarea, #main-wrapper fieldset {
    background-color: #090909;
    color: #DDDDDD;
}
.fieldset-description {
    color: #CCCCCC !important;
}
input {
    background-color: #090909;
    color: #DDDDDD;
}
#main {
    padding-top: 20px;
    margin-top: 0px !important;
}
}
