css:
@-moz-document domain("music.amazon.fr") {
.border-gradient2 .center {
    background-color: #000;
}
.border-gradient2 .center *
{
    color: #727272;
}
.playlistDetailsListItem * {

}

.navButton.left-align-text {
    background-color: #232939;
}

.navButton.left-align-text * {
    color: #727272 !important;
}

.horizontalTile .details .primaryTitle, .horizontalTile .details .primaryTitle a, .horizontalTile .detailsWithIndex .primaryTitle, .horizontalTile .detailsWithIndex .primaryTitle a {
    color: #657fc9;
    font-size: .9375rem;
}
.navigationToggle .backgroundImageGradient {
    background-image: linear-gradient(180deg,hsla(0, 0%, 0%, 0.19),hsla(0, 0%, 0%, 0.42),#000);
}
.playlistDetailsListItem:hover:not(.emptyRow):not(.header) td {
    background-color: #3d759f61;
}
.playlistDetailsListItem:hover:not(.emptyRow):not(.header) .hoverColumn, .playlistDetailsListItem:hover:not(.emptyRow):not(.header) .selectColumn {
    border-bottom: 1px solid #3d759f61;
}
.playlistDetailsListItem:not(.emptyRow) td {
    border-bottom: 1px solid #3d759f61;
}
}
