css:
@-moz-document domain("zportal.nl") {
.zermeloSideMenu {
    background-color: #ffff00;
}
.zermeloTitleSmall {
    color: #000000;
}
.zermeloSideMenu,.zermeloSideMenuItem {
    background-color: #ffff00;
}
.zermeloSideMenuItem:hover {
    background-color: #ffff00;
}
.mainMenuIcon {
    background-color: #ffff00;
}
div[eventproxy="isc_Img_1"], div[eventproxy="isc_Img_3"], div[eventproxy="isc_Img_5"] {
    filter: brightness(0);
}
.zermeloTopBar {
    filter: saturate(0);
}
a, *, h2 {
    color: #ff0;
}
.zermeloModule * {
    color: #000000
}
.head {
    filter: hue-rotate(70deg) brightness(2);
}
}
