css:
@-moz-document domain('sis.ssakhk.cz'){
body {
    background-color:#191919;
    color: white;
}
.timetable > :nth-child(odd) {
    background-color: #2e2119;
}
.col .hour-line label{
color: white;
}
.alert-success {
    background-color: #222;
    border-color: #222;
}
.slider {
    background-color: #222;
}
.panel {
    background-color: #222;
    color:white;
    border-color: #222;
}
.panel-default > .panel-heading {
    background-color: gray;
}
.panel > .panel-heading {
    background-color: gray;
}
.table.table-column-striping tr td:nth-child(even) {
background-color: #222;
}
.table .hour {
background-color: #ffa500;
}
.hour.hour-excused{
background-color: #00D000;
}
.hour.hour-delay{
background-color: greenyellow;
}
.hour.hour-absent {
background-color: #da2d2d;
}
}

@-moz-document url-prefix('https://sis.ssakhk.cz/TimeTable'){
img{
filter: invert(90%);
}
}
