css:
@-moz-document domain("dapenti.com") {
body, td{
    font-family: "Microsoft YaHei", PingFangSC-Regular, STHeiti, "黑体";
}
body{
    background: #ddd;
}
a:visited{
    color: #609 !important;
}


.box2, .box3, .box4,
.ke-zeroborder,
.oblog_t_2{
    border: none;
    background: #f6f6f6;
}
.box2, .box3, .box4,
.oblog_t_1{
    box-sizing: content-box;
    box-shadow: 0 2px 8px rgba(0,0,0,.1)
}

#right .title,
.center_title{
    color: #003399;
    font-size: 21px;
    font-weight: normal;
    letter-spacing: 0;
    background: #eee;
    border: none;
    border-bottom: 1px solid #ccc;
}
#right .title a:link,
.center_title a:link{
    color: #039;
}

.center_title_down{
    border: none;
}

ul{
    padding: 0;
    line-height: 200%;
    list-style-type: none;
}

ul a::before{
    padding: 0 8px 0 0;
    content: "•";
    color: #666;
    opacity: .5;
}

#right a{
    letter-spacing: 0;
}
#right .title{
    text-align: left;
}
#right .title p{
    margin: 4px;
}



.oblog_t_1{
    padding: 8px;
}
.style1 a{
    font-size: 21px !important;
}
}
