css:
@-moz-document url-prefix("https://vle.chesterfield.ac.uk/") {
body,
footer,
.page-footer,
html {
    background-color: #000!important;
}

\\ Head #page-header,
#page-header-wrapper,
#above-header,
#navwrap {
    background-color: #333!important;
    border-bottom-color: #444;
    color: #aaa!important;
}


.content,
.header,
section,
.container,
.course-footer,
.row-fluid,
.left-col,
.span12,
#page-footer,
#page-footer p {
    background-color: #333!important;
    border-color: #fff!important;
    color: #aaa!important;
}

a,
p,
h1,
h2,
h3,
h4,
h5,
h6,
.navbar .nav li a,
.courses .coursebox.even,
.courses .coursebox {
    color: #aaa!important;
}

.courses .coursebox {
    background-color: #444!important;
}

.sectionname {
    background-color: #444!important;
    color: #aaa!important;
}

span.activity-date-overdue {
    background-color: #b55!important;
}

div.twitter-timeline,
.timeline-Widget {
    background: #444 !important;
    color: #aaa !important;
}

strong {
    color: #aaa!important;
}

span.activity-date-submitted {
    background-color: #5b5;
}

h5,
h5 span {
    color: #aaa!important;
    background-color: #444!important;
}

select,
.breadcrumb {
    color: #aaa!important;
    background-color: #555!important;
}

.dropdown,
.dropdown-menu,
.dropdown-menu li a:hover {
    background-color: #333;
}

.nav .dropdown-menu li a:hover,
.navbar .nav li a:hover {
    background-color: #555!important;
}

section div {
    background-color: #333!important;
    color: #aaa!important;
}

table {
    background-color: #444!important;
}
}
