css:
@-moz-document url-prefix("http://jira."), url-prefix("https://jira."), url-prefix("http://j.proje") {

/*[[fontface]]*/


* {
    line-height: 1.42857143;
    letter-spacing: 0;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-rendering: optimizeLegibility !important;
}

:root {
  /*[[colorscheme]]*/
}



/* header colors */
.aui-header {
    background: var(--main-header-bgcolor);
}

.aui-header .aui-dropdown2-trigger.active, .aui-header .aui-dropdown2-trigger:hover, .aui-header a:hover {
    background: var(--main-header-button-hover);
}

/*Help Buttons*/
.aui-iconfont-help {
    color: yellowgreen !important;
}

/*Titles*/
h1 {
    font-size: 16pt !important;
    line-height: 20pt !important;
    vertical-align: middle
}


/* Create button*/
#create_link {
    font-weight: normal;
    background: var(--create-button-bgcolor)
}


.aui-list a:link,
.aui-list a:visited {
    color: black;
    text-decoration: none;
}

.aui-list a.active,
.aui-list .aui-list-item.active > a {
    background-color: var(--list-bgcolor-active);
    color: black;
}

.aui-list {
    background-color: var(--list-bgcolor);    
}

form#issue-filter .check-list-item.active, form.aui .check-list-item.active, .check-list-item.active, .check-list-group-footer.active {
    background-color: var(--list-bgcolor-active);
    color: black;    
}

/* Action Links -> Always show */
.action-links {
    visibility: visible !important
}

/*Time*/
time.livestamp {
    color: rgb(47, 174, 44) !important
}

.aui-dropdown2.aui-style-default {
    background-color: var(--list-bgcolor);    
}

aui-inline-dialog .aui-inline-dialog-contents {
    background-color: var(--list-bgcolor);      
}

.aui-dropdown2.aui-style-default:hover .aui-dropdown2-checkbox:not(.aui-dropdown2-disabled):hover,
.aui-dropdown2.aui-style-default:hover .aui-dropdown2-radio:not(.aui-dropdown2-disabled):hover,
.aui-dropdown2.aui-style-default:hover a:not(.aui-dropdown2-disabled):hover {
    background-color: var(--list-bgcolor-active);
    color: black;
}


/* buttons */
.aui-button, a.aui-button, .aui-button:visited {
    background-color: var(--main-button-bgcolor);
}

.aui-button.aui-button-primary, .aui-button.aui-button-primary:visited {
    background-color: var(--primary-button-bgcolor);    
    font-weight: normal;
}

.aui-button.aui-button-primary:hover {
    background-color: var(--primary-button-bgcolor-hover);    
    font-weight: normal;
}


.aui-button.active, a.aui-button.active, .aui-button:hover, a.aui-button:hover, .aui-button:visited:hover {
    background-color: var(--main-button-bgcolor-hover);
}


ul.project-issuetypes>li>a:focus, ul.project-issuetypes>li>a:hover {
    background-color: rgba(9,30,66,0.08);    
}

.gadget .dashboard-item-header > .dashboard-item-title {
    font-weight: normal;
}

/* headers */
#gh, .aui-page-header, .issue-header, #project-config-header, #content .aui-sidebar+.aui-page-header, .issue-header .issue-header-content>.aui-page-header  {
    background-color: var(--header-bgcolor);        
}

/* sidebars */
.aui-sidebar-body, .aui-sidebar-footer, .navigator-sidebar, .aui-page-panel-nav {
    background-color: var(--sidebar-bgcolor) !important;              
}

.gadget.color1 .dashboard-item-header {
    background-color: var(--gadget-header-bgcolor);
}

/* ------------------------- layout fixes ------------------------- */

.ghx-issue {
    font-size: 13px;
}

/* Issue details, people and time tracking column widths */
.item-details dl>dt, dt.tt_text {
    width: 100px;
}

.project-config-panel-header  {
    padding: 10px !important; 
}

.aui-dropdown2 [role="menuitem"], .aui-dropdown2 [role="menuitemcheckbox"], .aui-dropdown2 [role="menuitemradio"], .aui-dropdown2 [role="radio"], .aui-dropdown2 [role="checkbox"], .aui-dropdown2 a {
    padding: 5px;
}

/*Ticket List larger*/
.navigator-content .navigator-results {
    margin: 0px;
    padding: 0px !important;
}


/*Scroll list spacing*/
.aui-list .aui-list-item-link {
    padding: 1px 10px;
}

/*Annoucement Banner*/
#announcement-banner {
    display: none;
}

/*Footer Information*/
#footer {
    display: none;
}

/*Remove Logo on left of title */
.aui-page-header-image {
    display: none;
}

/*Remove padding above title*/
#stalker > div > header {
    padding: 5px !important
}

/*Drop Down Menu*/
form.aui .select {
    height: 25px !important;
    line-height: 25px!important;
    padding: initial
}

/* issue search */
.saved-search-selector {
    padding: 10px;
    background-color: var(--header-bgcolor);        
}

form.aui.navigator-search.query-component.generic-styled {
    margin: 0px;
    padding: 5px 10px;
    background-color: var(--header-bgcolor);        
}
}
