css:
@-moz-document domain("zportal.nl") {
.zermeloSideMenu {
    background-color: #ff00ff;
}
.zermeloTitleSmall {
    color: #000000;
}
.zermeloSideMenu,.zermeloSideMenuItem {
    background-color: #F0f;
}
.zermeloSideMenuItem:hover {
    background-color: #ff00ff;
}
.mainMenuIcon {
    background-color: #ff00ff;
}
div[eventproxy="isc_Img_1"], div[eventproxy="isc_Img_3"], div[eventproxy="isc_Img_5"] {
    filter: brightness(0);
}
.zermeloTopBar {
    filter: saturate(0);
}
a, *, h2 {
    color: #ff00ff;
}
.zermeloModule * {
    color: #000000
}
.head {
    filter: saturate(1.2) hue-rotate(-30deg);
}
}
