css:
@-moz-document url-prefix("https://nela.com.br/") {
body {
    background-color: #424242;
    background-image: linear-gradient(to right, #512DA8, #00BCD4);
    color: #424242;
}
.climate-bg {
    background-color: #424242;
    color: #fff;
}
.fixed-sidebar-left {
    background: #424242;
    box-shadow: none;
    border-right: none;
    color: #fff;
}
.today-events-thumb {
    background: #424242;
}
.ui-block {
    border-radius: 10px 10px;
    border: none;
}
.ui-block .ui-block-title {
    border-radius: 8px 8px;
}
.fixed-sidebar-left .left-menu li a .left-menu-icon {
    color: #fff;
    border-right: none;
}
#site-header {
    background-color: #424242;
    padding-right: 20px;
}
.loading-nela .text {
    color: #fff;
}
.btn-primary {
    color: #fff;
    background-color: #007bff;
    background-image: linear-gradient(to right, #00BCD4, #512DA8);
    border-color: #007bff;
}
.nela-bg {
    background-color: #0078d7;
    background-image: linear-gradient(to right, #00BCD4, #512DA8);
    color: #fff;
}
.schedule-bg {
    background-color: #6f74a1;
    background-image: linear-gradient(to right, #00BCD4, #512DA8);
    color: #fff;
}
.img-thumbnail {
    border-radius: 50%;
}
.bg-color-blue-green {
    background-color: #512DA8;
}
.climate-bg {
    background-color: #424242;
    background-image: linear-gradient(to right, #00BCD4, #512DA8);
    color: #fff;
}
.ui-block-title {
    background-color: #0078d7;
    background-image: linear-gradient(to right, #00BCD4, #512DA8);
    border-bottom: none;
}
.sidebar--large .left-menu>li .left-menu-icon {
    background: #424242;
}
}
