css:
/* i really want this to be global */
.panel-title-text {
	font-size: x-large;
}

.panel-menu-toggle {
	font-size: x-large;
}

.panel-content {
	height: calc(100% - 44px);
}

div.flot-tick-label {
	font-size: x-large;
}

.graph-legend-series {
	font-size: 22px;
}

.graph-legend-icon {
	top: 2px;
}

.graph-legend-icon .fa {
	font-size: 175%;
	top: 4px;
}

.axisLabel {
	font-size: 24px;
}

.panel-content {
	padding: 10px 10px;
}

.left-yaxis-label {
	left: 4px;
}

.right-yaxis-label {
	right: 4px;
}

div.flot-x-axis>div.flot-tick-label {
	font-size: large;
}

.grafana-tooltip {
	font-size: 24px;
}
