css:
@-moz-document domain("theoldreader.com") {
.container-fluid > .row > .col-md-3 {
    display: none!important;
}

.container-fluid > .row > .col-md-9 ul.nav li.visible-lg-inline {
    display: none!important;
}

div.col-md-9 {
    width: 100%;
}

form.navbar-form {
    margin-top: 0;
    margin-bottom: 0;
}

.navbar-nav > li > a {
    padding-top: 11px;
    padding-bottom: 11px;
}

.navbar {
    min-height: 40px;
}

div.main-container {
    padding-top: 48px;
}

/* feed */

span.likes, a.btn-like {
    display: none!important;
}
}
