css:
@-moz-document domain("www.thingiverse.com")
{
    #ui-datepicker-div {
        background:rgba(255,255,255, .9);
        padding:10px 20px;
        border:1px solid #ddd;
        border-top-width:8px;
        border-radius:0 0 10px 10px;
    }
    .ui-datepicker-header {
        padding:5px 0;
    }
    .ui-datepicker-prev, .ui-datepicker-next {
        cursor: pointer;
        background-color:#5BA2F7;
        padding:3px 5px;
        color:#fff;
        border-radius:5px;
    }
    .ui-datepicker-prev {
        float:left;
    }
    .ui-datepicker-next {
        float:right;
    }

    .ui-datepicker-title {
        text-align:center;
        font-weight:600;
        font-size:13px;
        padding:5px 0;
    }

    .ui-datepicker-calendar th {
        padding:6px;
        text-align:center;
    }
}
