css:
@-moz-document domain("www.dumbingofage.com") {
body.custom-background {
    background-color: #222;
    color: #ccc;
}
div#content-wrapper {
    background-color: #333;
    color: #ccc;
}
div#menubar-wrapper {
    background-color: #445;
    color: #ccd;
    text-shadow: black 0px 0px 5px;
}
div.post {
    background-color: #222;
    color: #ccc;
    border: solid #778 3px;
    border-bottom: none;
}
div.post a, div.comic_navi_wrapper a {
    color: #88c;
}
div.widget a {
    color: #88c;
}
div.widget {
    color: #aaa;
    text-shadow: black 0px 0px 3px;
}
div#comic {
    background-color: #888;
}
}
